mapper.select:
SELECT H.*, P.PRODUCTNAME, P.ITEMCODE, P.UNITPRICE
FROM HOTELSTOCK AS H
JOIN PARTNERSTOCK AS P ON H.ITEMID = P.ITEMID;

mapper.update:
UPDATE HOTELSTOCK
SET ORDERSTATUS  = '발주중'
WHERE INVENTORYID = #{inventoryId}

UPDATE HOTELSTOCK
SET ORDERSTATUS  = '양호'
WHERE INVENTORYID = #{inventoryId}
